import React, { useState } from 'react';
import './DataTable.css';
import { Button, Form } from 'react-bootstrap';
import Modal from './Modal';
import axios from 'axios';
const DataTable = ({ data, fetchData }) => {
  const [showEdit, setShowEdit] = useState(false);
  const [showDelete, setShowDelete] = useState(false);
  const [user, setUser] = useState({});

  const handleCloseEdit = () => setShowEdit(false);
  const handleShowEdit = (user) => {
    setUser(user);
    setEmail(user.email);
    setName(user.name);
    setShowEdit(true);
  };
  const handleCloseDelete = () => setShowDelete(false);
  const handleShowDelete = (user) => {
    console.log(user);
    setUser(user);
    setShowDelete(true);
  };

  const [loading, setLoading] = useState(false);
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const editUser = async (e) => {
    setLoading(true);
    e.preventDefault();
    try {
      let url = `http://localhost:3001/user/update/${user.id}`;
      let res = await axios.patch(
        url,
        { name, email, password },
        {
          headers: {
            Authorization: `Bearer ${user.token}`,
          },
        }
      );
      handleCloseEdit();
      setUser({});
      return;
    } catch (error) {
      console.log(error);
      setError(error.response.data.message);
    } finally {
      setLoading(false);
    }
  };

  const deleteUser = async (e) => {
    setLoading(true);
    e.preventDefault();
    console.log(user);
    try {
      let url = `http://localhost:3001/user/delete/${user.id}`;
      let res = await axios.delete(url);
      handleCloseDelete();
      setUser({});
      fetchData();
      return;
    } catch (error) {
      console.log(error);
      setError(error.response.data.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Name</th>
          <th>email</th>
          <th>Date Created</th>
          <th>Actions</th>
        </tr>
      </thead>
      <tbody>
        {data.map((value) => (
          <tr>
            <td>{value.name}</td>
            <td>{value.email}</td>
            <td>{value.createdAt}</td>
            <td className="d-flex">
              <Button variant="secondary" className="px-4" onClick={() => handleShowEdit(value)}>
                Edit
              </Button>
              <Button variant="danger" onClick={() => handleShowDelete(value)}>
                Delete
              </Button>
            </td>
          </tr>
        ))}
      </tbody>
      <Modal show={showEdit} handleClose={handleCloseEdit} title="Edit User">
        <Form>
          <Form.Group className="pb-3" controlId="exampleForm.ControlTextarea1">
            <Form.Label>Name</Form.Label>
            <Form.Control type="text" placeholder="Enter your name" value={name} onChange={(e) => setName(e.target.value)} />
          </Form.Group>
          <Form.Group className="pb-3" controlId="exampleForm.ControlTextarea2">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" value={email} onChange={(e) => setEmail(e.target.value)} />
          </Form.Group>
          <Form.Group controlId="exampleForm.ControlTextarea3">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Enter password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </Form.Group>
          {error ? <span>{error}</span> : ''}

          <div className="d-flex justify-content-end mt-3">
            <button onClick={editUser} disabled={loading} className="px-4">
              {loading ? 'Editing...' : 'Edit'}
            </button>
          </div>
        </Form>
      </Modal>
      <Modal show={showDelete} handleClose={handleCloseDelete} title="Delete User">
        <p>Are you sure you want to delete {user.name}?</p>
        <div className="d-flex justify-content-end mt-3">
          <Button variant="danger" onClick={deleteUser} disabled={loading}>
            {loading ? 'Deleting...' : 'Delete'}
          </Button>
        </div>
      </Modal>
    </table>
  );
};

export default DataTable;
